mapper.insert:
insert into CHOOSE (CHID, SID, SEAT ,STATE
  )
values (#{chid,jdbcType=DECIMAL}, #{sid,jdbcType=DECIMAL}, #{seat,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}
  )

mapper.update:
update CHOOSE
set SID = #{sid,jdbcType=DECIMAL},
  SEAT = #{seat,jdbcType=VARCHAR},
  STATE = #{state,jdbcType=VARCHAR}
where CHID = #{chid,jdbcType=DECIMAL}

mapper.select:
