mapper.update:
update ORDERT
set VID = #{vid,jdbcType=DECIMAL},
  SID = #{sid,jdbcType=DECIMAL},
  NUM = #{num,jdbcType=DECIMAL},
  OTIME = #{otime,jdbcType=TIMESTAMP}
where OID = #{oid,jdbcType=DECIMAL}

mapper.select:
select OID, VID, SID, NUM, OTIME
from ORDERT
where OID = #{oid,jdbcType=DECIMAL}
